#!/usr/bin/env node

"use strict"




const constants = require('pdir/commons/constants')





const doc = `
Name:
	pdir â€” partition a folder into several by file-extension.

Usage:
	pdir [-e EXTS ...] [-n | --dry-run] [-c | --copy] [-d DIR | --dest DIR] [--] SRC...
	pdir (-h | --help | --version)

Description:

	Pdir does not follow symlinks.

Arguments:
	SRC                           The directory to partition into several other directories. Can be repeated.

Options:
	-d, --dest DIR                The directory in which to save each created folder. [default: ${constants.workingDir}]
	-e EXTS                       The groups to separate into.
	-n, --dry-run                 Do everything except move the files.
	-h, --help                    Display this documentation.
`





const docopt = require('docopt').docopt
const pdir   = require('pdir/app/pdir')
const args   = docopt(doc)





pdir(args)
