
"use strict"




const constants = {
	canonicalDir: process.cwd( ),
	errCodes: {
		notFound: 'ENOENT'
	},
	statTypeMethods: [
		'isFile',
		'isDirectory',
		'isBlockDevice',
		'isCharacterDevice',
		'isSymbolicLink',
		'isFIFO',
		'isSocket'
	],
	events: {

		'partitioned': 'partitioned',
		'error':       'error',
		'close':       'close',
		'data':        'data',
		'end':         'end',

		'sourceChecksum': 'sourceChecksum',
		'targetChecksum': 'targetChecksum'

	},
	regexs: {
		extensionDelimiter: /[\s]*,[\s]*/g
	},
	hashes: {
		sha256: 'sha256'
	},
	extensions: {
		wildcard: '*'
	}

}

constants.statTypeMethods.forEach(type => {

	const modified             = type.replace(/^is/, '').toLowerCase( )
	constants.events[modified] = modified

})






module.exports = constants
